package kata.kyu4;

import org.junit.Test;

import static org.junit.Assert.assertEquals;

public class DecodeTheMorseCodeAdvancedTest {

    @Test
    public void testExampleFromDescription() {
        assertEquals("HEY JUDE", DecodeTheMorseCodeAdvanced.decodeMorse(DecodeTheMorseCodeAdvanced.decodeBits("1100110011001100000011000000111111001100111111001111110000000000000011001111110011111100111111000000110011001111110000001111110011001100000011")));
        assertEquals("E", DecodeTheMorseCodeAdvanced.decodeMorse(DecodeTheMorseCodeAdvanced.decodeBits("01110")));
        assertEquals("E", DecodeTheMorseCodeAdvanced.decodeMorse(DecodeTheMorseCodeAdvanced.decodeBits("1")));
        assertEquals("E", DecodeTheMorseCodeAdvanced.decodeMorse(DecodeTheMorseCodeAdvanced.decodeBits("111")));
        assertEquals("THE QUICK BROWN FOX JUMPS OVER THE LAZY DOG.", DecodeTheMorseCodeAdvanced.decodeMorse(DecodeTheMorseCodeAdvanced.decodeBits("")));
    }

}